var utils = require('sdk/window/utils');
let { Cu } = require('chrome');

function Widget(){}
Widget.prototype.initialize = function() {};
Widget.prototype.getName = function() {};
Widget.prototype.getThumbnailImageSource = function() {}
Widget.prototype.getSourceCode = function() {};
Widget.prototype.showEditWidgetButton = function() { return true; };
Widget.prototype.showRemoveWidgetButton = function() { return true; };
Widget.prototype.getSourceCode = function() {};
Widget.prototype.editWidget = function(evt) {
	evt.target.ownerDocument.defaultView.alert("A panel with a form to edit me!");
};
Widget.prototype.removeWidget = function(evt) {
	evt.target.parentElement.parentElement.remove();
};
Widget.prototype.getEditableInstanceConfigControls = function(document) {

	var controls = this.createEditableInstanceConfigContainer(document); 

    if(this.showEditWidgetButton()){
	    controls.appendChild(this.createEditWidgetButton(document));
    }
    if(this.showRemoveWidgetButton()){
	    controls.appendChild(this.createRemoveWidgetButton(document));
	}

    return controls;
};
Widget.prototype.createEditableInstanceConfigContainer = function(document) {

	var controls = document.createElement("div");
		controls.style["width"] = "100%";
    	controls.style["position"] = "relative";
    	controls.style["vertical-align"] = "top";
    	controls.style.zIndex = "2147483646";
    return controls;
}
Widget.prototype.createEditWidgetButton = function(document) {

	var me = this;
	var cog = document.createElement("img");
        cog.style.width = "20px";
        cog.src="data:image/png;base64,";
        cog.onclick = function(evt){
        	me.editWidget(evt);
        }
	return cog;
}
Widget.prototype.createRemoveWidgetButton = function(document) {

	var me = this;
	var rmv = document.createElement("img");
        rmv.style.width = "20px";
        rmv.src="data:image/png;base64,";
        rmv.onclick = function(evt){
        	me.removeWidget(evt);
        }
	return rmv;
}
Widget.prototype.getEditableInstance = function(document) {

	var widget = document.createElement("div");
		widget.appendChild(this.getEditableInstanceConfigControls(document));
		widget.appendChild(this.getMainWidgetElement(document));
	return widget;
};
Widget.prototype.getThumbnail = function(document) {

	var col = document.createElement("div");
		col.className = "col-lg-3 col-md-4 col-xs-6 thumb";

	var anchor = document.createElement("a");
		anchor.className = "thumbnail text-center";
		anchor.setAttribute("draggable", true); 
		anchor.setAttribute("href", "#");

	var thumbImage = document.createElement("img");
		thumbImage.className = "woa-app-maker-thumbnail img-responsive";
		thumbImage.src = this.getThumbnailImageSource();

		anchor.appendChild(thumbImage);
		anchor.appendChild(document.createTextNode(this.getName()));

	var me = this;
	anchor.addEventListener("dragend",function(e){
		e.preventDefault();
		e.stopImmediatePropagation();					
		var win = Cu.waiveXrays(utils.getMostRecentBrowserWindow().content);
		var elem = me.getEditableInstance(document); 
		win["insertElementIntoPageEditor"](elem);
    },false);
	
	col.appendChild(anchor);
	return col;
};
Widget.prototype.enableLoading = function(document){

    var loading = document.createElement("div");
        loading.id= "woa-full-loading";
        loading.className = "woa-loading";
        loading.onclick = function(){ this.remove(); }
    document.body.appendChild(loading);
}
Widget.prototype.disableLoading = function(document){

    var loading = document.getElementById("woa-full-loading");
    if(loading) loading.remove();
}


