function SearchWidget(){}
SearchWidget.prototype = new Widget();
SearchWidget.prototype.getName = function() {
	return "IO search";
};
SearchWidget.prototype.getThumbnailImageSource = function() {
	return "data:image/png;base64,";
};
SearchWidget.prototype.showEditWidgetButton = function() { return false; };
SearchWidget.prototype.getMainWidgetElement = function(document) {

	var group = document.createElement("div");
		group.className = "input-group";
		//group.setAttribute("contenteditable", true);

	var input = document.createElement("input");
		input.className = "form-control";
		input.setAttribute("placeholder","Search for...");
		input.setAttribute("type", "text");

	var btnGroup = document.createElement("span");
		btnGroup.className = "input-group-btn";
	var btn = document.createElement("btn");
		btn.className = "btn btn-default";
		btn.setAttribute("type","button");
	var btnIocn = document.createElement("span");
		btnIocn.className="glyphicon glyphicon-search";
		btnGroup.appendChild(btn);
		btn.appendChild(btnIocn);

	group.appendChild(input);
	group.appendChild(btnGroup);

	return group;
}



