InfoObjectWidget.prototype.showEditWidgetButton = function(document) { return false };
InfoObjectWidget.prototype.getEditableInstance = function(document) {

	var widget = document.createElement("div");
		widget.appendChild(this.getEditableInstanceConfigControls(document));
		widget.appendChild(this.getMainWidgetElement(document));
	return widget;
};
InfoObjectWidget.prototype.getThumbnail = function(document) {

	var container = document.createElement("div");
        container.className = "col-xs-6 col-md-3";
        container.id = this.io.id;
        container.setAttribute("draggable", true); 

    var thumbnail = container.appendChild(document.createElement("div"));
        thumbnail.className = "thumbnail text-center";

    var imgContainer = document.createElement("div");
        imgContainer.className = 'thumbnail-image-container'; 
        thumbnail.appendChild(imgContainer);

    var img = document.createElement("img");
        img.className = 'thumbnail-image'; 
        img.src = this.io.imageSrc; 
        imgContainer.appendChild(img);

    var caption = thumbnail.appendChild(document.createElement("div"));
        caption.className = "caption small-custom-caption";
        caption.innerHTML = this.io.name;

    var list = document.getElementById('info-objects-list');
        list.insertBefore(container, list.lastChild);

    var me = this;
	container.addEventListener("dragend",function(e){
		e.preventDefault();
		e.stopImmediatePropagation();					
		var win = Cu.waiveXrays(utils.getMostRecentBrowserWindow().content);
		var widget = me.getEditableInstance(document); 

		me.enableLoading(win.document);
		me.searchEngine.setDecoratorClass(me.searchEngine.getDecoratorClass(me.io.classname));
		me.searchEngine.loadStoredInstances(me.io.id, function(){

			//Despite the application of decorators, the clonning into a new context removes the 
			//provileged functionality, so we need to keep just the information of every instance and 
			//recreate the decorator when clicked

			me.loadInfoObjectsIntoWidget(me.searchEngine.currentInstances, widget, document); 
			
			win["insertElementIntoPageEditor"](widget);
			me.disableLoading(win.document);
		});

		
    },false);

    return container;
};
InfoObjectWidget.prototype.loadInfoObjectsIntoWidget = function(decoIOs, widget, document) {

	//var decoIOs = instances.wrappedJSObject;
	for (var i = 0; i < decoIOs.length; i++) {
				
		var wrapper = document.createElement('div');
			wrapper.style["box-shadow"] = "0px 1px 7px 0px rgb(170, 170, 170)";
			wrapper.style["margin"] = "1em 0px";
			wrapper.style["padding"] = "10px";
			wrapper.style["border-radius"] = "7px";
			wrapper.innerHTML= decoIOs[i].getDomElement();
			wrapper.setAttribute("contenteditable", false);

		if(wrapper) {
			var controls = this.createEditableInstanceConfigContainer(document);
				controls.appendChild(this.createMessagesButton(document, decoIOs[i]));
			wrapper.insertBefore(controls, wrapper.firstChild);
			widget.appendChild(wrapper);
		}
	}
}
InfoObjectWidget.prototype.getMainWidgetElement = function(document) {

	var row = document.createElement("div");
		row.className = "row";
	return row;
};


